package com.feedingtracker.backend;import java.util.Date;import java.util.UUID;import javax.persistence.Column;import javax.persistence.Entity;import javax.persistence.Id;import lombok.Data;import lombok.NoArgsConstructor;import org.hibernate.annotations.ColumnDefault;@NoArgsConstructor@Data@Entitypublic class Feeding {		@Id	private UUID id;		@Column(nullable = false)	private Date creationDate;		private String side;		@Column(nullable = false)	private String board;		@Column(nullable = false)	private Date lastModification;		@Column(nullable = false)	@ColumnDefault("false")	private boolean deleted;		@Column(nullable = false)	private int revision;	}