package com.feedingtracker.backend;import java.util.List;import java.util.Optional;import java.util.UUID;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.stereotype.Repository;@Repositoryinterface FeedingRepository extends JpaRepository<Feeding, UUID> {	List<Feeding> findByBoardOrderByCreationDateDesc(String board);	Optional<Feeding> findFirstByBoardOrderByRevisionDesc(String board);	List<Feeding> findByBoardAndRevisionGreaterThanOrderByRevisionDesc(String board, int revision);}